import axios from "axios";

//const url = ;

// The getRecipes method retrieves recipes from the server
// It accepts a "query" or term to search the recipe api for
export default {
  getRecipes: function(query) {
    axios.get(`https://www.googleapis.com/books/v1/volumes?q=harrypotter+inauthor:rowling`).then(response => console.log(response.data))
    .then((data) => {

      console.log(data)
     })
  }
};

